import React, { useState } from "react";

function Professor() {
  const [text, setText] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleCorrect = async () => {
    setLoading(true);
    setResult(null);

    try {
      const response = await fetch("http://127.0.0.1:5000/correct", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ text }),
      });

      const data = await response.json();
      setResult(data);
    } catch (error) {
      alert("Erreur de connexion au backend");
    }

    setLoading(false);
  };

  return (
    <div style={{ padding: "40px" }}>
      <h1>Mode Professeur</h1>

      <textarea
        placeholder="Collez ici la copie de l’élève..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        rows={10}
        style={{ width: "100%", marginTop: "20px" }}
      />

      <button
        onClick={handleCorrect}
        disabled={loading || !text}
        style={{ marginTop: "20px", padding: "10px 20px" }}
      >
        {loading ? "Correction en cours..." : "Corriger"}
      </button>

      {result && (
        <div style={{ marginTop: "30px" }}>
          <h3>Résultat</h3>
          <p><strong>Note :</strong> {result.score}/20</p>
          <p><strong>Commentaire :</strong> {result.comment}</p>
          <p><strong>Détails :</strong> {result.details}</p>
        </div>
      )}
    </div>
  );
}

export default Professor;
