import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";

function CorrectionLibre() {
  const [texte, setTexte] = useState("");
  const [resultat, setResultat] = useState("");
  const [loading, setLoading] = useState(false);
  const [fichiers, setFichiers] = useState([]);

  // üîπ Fonction stable (corrige le warning useEffect)
  const lancerCorrection = useCallback(async () => {
    if (fichiers.length === 0) return;

    setLoading(true);
    setResultat("");

    try {
      const formData = new FormData();
      fichiers.forEach((file) => {
        formData.append("images", file);
      });

      const response = await axios.post(
        "http://127.0.0.1:5000/ocr",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setResultat(response.data.texts.join("\n\n"));
    } catch (error) {
      console.error(error);
      setResultat("‚ùå Erreur lors de la correction.");
    } finally {
      setLoading(false);
    }
  }, [fichiers]);

  // üîπ Lance automatiquement la correction quand on ajoute des fichiers
  useEffect(() => {
    lancerCorrection();
  }, [lancerCorrection]);

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>üìò CORRECTION AUTO ‚Äì Correction Libre</h2>

      <div style={styles.card}>
        <input
          type="file"
          multiple
          accept="image/*,.pdf"
          onChange={(e) => setFichiers(Array.from(e.target.files))}
          style={styles.input}
        />

        <button
          onClick={lancerCorrection}
          disabled={loading || fichiers.length === 0}
          style={styles.button}
        >
          {loading ? "‚è≥ Correction en cours..." : "üöÄ Lancer la correction"}
        </button>
      </div>

      <div style={styles.resultBox}>
        <h3>üß† R√©sultat OCR</h3>
        <pre style={styles.resultText}>
          {resultat || "Aucun r√©sultat pour le moment."}
        </pre>
      </div>
    </div>
  );
}

export default CorrectionLibre;

/* üé® Styles simples premium */
const styles = {
  container: {
    maxWidth: "900px",
    margin: "40px auto",
    padding: "20px",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    textAlign: "center",
    marginBottom: "30px",
  },
  card: {
    padding: "20px",
    borderRadius: "10px",
    background: "#f8f9fa",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
    marginBottom: "30px",
  },
  input: {
    display: "block",
    marginBottom: "15px",
  },
  button: {
    padding: "12px 20px",
    borderRadius: "8px",
    border: "none",
    background: "#2563eb",
    color: "#fff",
    fontSize: "16px",
    cursor: "pointer",
  },
  resultBox: {
    padding: "20px",
    borderRadius: "10px",
    background: "#ffffff",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
  },
  resultText: {
    whiteSpace: "pre-wrap",
    fontSize: "14px",
  },
};
