import { Link } from "react-router-dom";

function Home() {
  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h1 style={styles.title}>CORRECTION AUTO</h1>
        <p style={styles.subtitle}>
          Plateforme intelligente d’évaluation et de correction automatisée
        </p>

        <div style={styles.buttons}>
          <Link to="/examen" style={styles.primaryBtn}>
            Mode Examen
          </Link>

          <button style={styles.secondaryBtn}>
            Tableau de bord
          </button>
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: {
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  card: {
    background: "rgba(255,255,255,0.08)",
    padding: "40px",
    borderRadius: "16px",
    width: "90%",
    maxWidth: "480px",
    textAlign: "center",
    backdropFilter: "blur(10px)",
    boxShadow: "0 20px 40px rgba(0,0,0,0.4)",
  },
  title: {
    fontSize: "32px",
    marginBottom: "10px",
  },
  subtitle: {
    fontSize: "16px",
    opacity: 0.85,
    marginBottom: "30px",
  },
  buttons: {
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  primaryBtn: {
    padding: "14px",
    background: "#00e0ff",
    color: "#000",
    borderRadius: "10px",
    textDecoration: "none",
    fontWeight: "bold",
  },
  secondaryBtn: {
    padding: "14px",
    background: "transparent",
    color: "#fff",
    border: "1px solid rgba(255,255,255,0.4)",
    borderRadius: "10px",
    cursor: "pointer",
  },
};

export default Home;
