import React from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import CorrectionLibre from "./CorrectionLibre";

function Home() {
  const navigate = useNavigate();

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h1 style={styles.title}>CORRECTION AUTO</h1>

        <p style={styles.subtitle}>
          Plateforme intelligente d‚Äô√©valuation et de correction automatis√©e
        </p>

        <div style={styles.buttons}>
          <button style={styles.mainButton}>
            üéì Mode Examen
          </button>

          <button
            style={styles.secondaryButton}
            onClick={() => navigate("/correction")}
          >
            ‚úçÔ∏è Correction Libre
          </button>

          <button style={styles.secondaryButton}>
            üë®‚Äçüè´ Espace Professeur
          </button>
        </div>
      </div>
    </div>
  );
}

function App() {
  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/correction" element={<CorrectionLibre />} />
    </Routes>
  );
}

const styles = {
  page: {
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background:
      "radial-gradient(circle at top, #1f2933 0%, #0f172a 40%, #020617 100%)",
    fontFamily: "Segoe UI",
  },

  card: {
    width: "90%",
    maxWidth: "420px",
    padding: "40px 30px",
    borderRadius: "24px",
    background: "rgba(255, 255, 255, 0.06)",
    backdropFilter: "blur(14px)",
    boxShadow: "0 25px 60px rgba(0,0,0,0.6)",
    textAlign: "center",
    color: "white",
  },

  title: { fontSize: "28px", marginBottom: "12px" },
  subtitle: { fontSize: "14px", marginBottom: "30px", opacity: 0.85 },

  buttons: {
    display: "flex",
    flexDirection: "column",
    gap: "16px",
  },

  mainButton: {
    padding: "15px",
    borderRadius: "16px",
    border: "none",
    fontWeight: "bold",
    background: "linear-gradient(90deg,#22d3ee,#38bdf8)",
    cursor: "pointer",
  },

  secondaryButton: {
    padding: "14px",
    borderRadius: "16px",
    border: "1px solid rgba(255,255,255,0.25)",
    background: "transparent",
    color: "white",
    cursor: "pointer",
  },
};

export default App;
