import React, { useState } from "react";

function CorrectionLibre() {
  const [files, setFiles] = useState([]);
  const [status, setStatus] = useState("");

  const handleFileChange = (e) => {
    setFiles([...e.target.files]);
  };

  const sendToBackend = async () => {
    if (files.length === 0) {
      alert("Veuillez s√©lectionner au moins un fichier");
      return;
    }

    const formData = new FormData();
    files.forEach((file) => {
      formData.append("files", file);
    });

    try {
      setStatus("‚è≥ Envoi des fichiers...");
      const response = await fetch("http://127.0.0.1:5000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setStatus("‚úÖ " + data.message);
    } catch (error) {
      console.error(error);
      setStatus("‚ùå Erreur de connexion au backend");
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>CORRECTION AUTO</h1>
      <p style={styles.subtitle}>
        Importez des copies (photos ou PDF) pour correction
      </p>

      <input
        type="file"
        multiple
        accept="image/*,.pdf"
        onChange={handleFileChange}
        style={styles.input}
      />

      <button style={styles.button} onClick={sendToBackend}>
        üöÄ Lancer la correction
      </button>

      {status && <p style={styles.status}>{status}</p>}
    </div>
  );
}

const styles = {
  container: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #0f2027, #203a43, #2c5364)",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Arial, sans-serif",
    padding: "20px",
  },
  title: {
    fontSize: "36px",
    fontWeight: "bold",
    marginBottom: "10px",
  },
  subtitle: {
    fontSize: "16px",
    marginBottom: "30px",
    opacity: 0.8,
  },
  input: {
    marginBottom: "20px",
    backgroundColor: "#fff",
    padding: "10px",
    borderRadius: "6px",
  },
  button: {
    backgroundColor: "#00c6ff",
    border: "none",
    padding: "12px 25px",
    borderRadius: "30px",
    fontSize: "16px",
    fontWeight: "bold",
    cursor: "pointer",
    color: "#000",
  },
  status: {
    marginTop: "20px",
    fontSize: "14px",
  },
};

export default CorrectionLibre;
