import React from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Professor from "./Professor";
import Exam from "./Exam";

const btnStyle = {
  padding: "14px 26px",
  fontSize: "16px",
  background: "#22c55e",
  border: "none",
  borderRadius: "6px",
  cursor: "pointer",
  color: "#000",
};

function Home() {
  const navigate = useNavigate();

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        background: "#0f172a",
        color: "white",
        fontFamily: "Arial, sans-serif",
        textAlign: "center",
      }}
    >
      <h1 style={{ fontSize: "42px" }}>CORRECTION AUTO</h1>
      <p style={{ fontSize: "18px", maxWidth: "600px" }}>
        Plateforme intelligente de correction automatis√©e des copies scolaires
      </p>

      <div style={{ marginTop: "30px", display: "flex", gap: "20px" }}>
        <button style={btnStyle} onClick={() => navigate("/professeur")}>
          Mode Professeur
        </button>
        <button style={btnStyle} onClick={() => navigate("/examen")}>
          Mode Examen
        </button>
      </div>
    </div>
  );
}

function App() {
  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/professeur" element={<Professor />} />
      <Route path="/examen" element={<Exam />} />
    </Routes>
  );
}

export default App;
