import React, { useState } from "react";
import axios from "axios";
import "./CorrectionLibre.css";

export default function CorrectionLibre() {
  const [texte, setTexte] = useState("");
  const [resultat, setResultat] = useState("");
  const [loading, setLoading] = useState(false);

  const lancerCorrection = async () => {
    if (!texte.trim()) {
      alert("Veuillez entrer un texte Ã  corriger.");
      return;
    }

    setLoading(true);
    setResultat("");

    try {
      const response = await axios.post("http://127.0.0.1:5000/corriger", {
        texte: texte,
      });

      setResultat(response.data.correction);
    } catch (error) {
      console.error(error);
      setResultat("Erreur lors de la correction.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="correction-container">
      <h1 className="title">ðŸ“˜ CORRECTION AUTO</h1>

      <textarea
        className="zone-texte"
        placeholder="Collez ici le texte ou la copie Ã  corriger..."
        value={texte}
        onChange={(e) => setTexte(e.target.value)}
      />

      <button
        className="btn-correction"
        onClick={lancerCorrection}
        disabled={loading}
      >
        {loading ? "Correction en cours..." : "Lancer la correction"}
      </button>

      {resultat && (
        <div className="resultat">
          <h2>RÃ©sultat</h2>
          <p>{resultat}</p>
        </div>
      )}
    </div>
  );
}
