import React, { useState } from "react";
import { corrigerTexte } from "./services/api";
import "./correctionlibre.css";

export default function CorrectionLibre() {
  const [texte, setTexte] = useState("");
  const [resultat, setResultat] = useState(null);
  const [loading, setLoading] = useState(false);
  const [erreur, setErreur] = useState("");

  const lancerCorrection = async () => {
    if (!texte.trim()) {
      setErreur("Veuillez entrer un texte à corriger.");
      return;
    }

    setLoading(true);
    setErreur("");
    setResultat(null);

    try {
      const data = await corrigerTexte(texte);
      setResultat(data.resultat);
    } catch (e) {
      setErreur("Erreur lors de la correction.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="correction-container">
      <h1>Correction Auto ✨</h1>

      <textarea
        placeholder="Collez ici le texte à corriger..."
        value={texte}
        onChange={(e) => setTexte(e.target.value)}
      />

      <button className="btn-primary" onClick={lancerCorrection}>
        {loading ? "Correction en cours..." : "Lancer la correction"}
      </button>

      {erreur && <p className="error">{erreur}</p>}

      {resultat && (
        <div className="resultat">
          <h2>Résultat</h2>
          <p><strong>Note :</strong> {resultat.note}/20</p>
          <p><strong>Commentaire :</strong> {resultat.commentaire_general}</p>

          <h3>Détails</h3>
          <ul>
            {resultat.details.map((item, index) => (
              <li key={index}>
                Ligne {item.ligne} — {item.erreur} → {item.correction}
              </li>
            ))}
          </ul>

          <h3>Conseils</h3>
          <ul>
            {resultat.conseils.map((c, i) => (
              <li key={i}>{c}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
